tinymce.PluginManager.add("azembed",function(t,e){t.addButton("azembed",{title:"Embed HTML",icon:"code",onclick:function(){t.windowManager.open({title:"Embed HTML",width:500,height:200,body:[{type:"textbox",multiline:!0,rows:10,cols:20,name:"html",label:"Embed HTML"}],onsubmit:function(e){t.insertContent(e.data.html)}})}})});
